import React from "react";
import Carousel from "react-bootstrap/Carousel";

// Impor gambar-gambar
import image1 from "/assets/images/slider/ISIF 2024-89.jpg";
import image2 from "/assets/images/slider/ISIF 2024-124.jpg";
import image3 from "/assets/images/slider/ISIF 2024-185.jpg";

// Array gambar
const images = [image1, image2, image3];

const ChatBanner = () => {
  const mystyle = {
    color: "white",
    fontSize: "clamp(16px, 2vw, 32px)",
  };

  return (
    <Carousel fade={true}>
      {images.map((imgSrc, index) => (
        <Carousel.Item key={index} interval={2000}>
          <img className="d-block w-100" src={imgSrc} alt={`Slide ${index + 1}`} />
          <Carousel.Caption>
            <h1 className="fw-bold" style={mystyle}>
              Coming Soon 2025
            </h1>
            <a
              className="btn btn-primary"
              href="/homeregist"
              rel="noopener noreferrer"
            >
              Regist Here
            </a>
          </Carousel.Caption>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default ChatBanner;
