import React from 'react';

const images = [
  "../img/slider/ISIF 2024-45.jpg",
  "../img/slider/ISIF 2024-74.jpg",
  "../img/slider/ISIF 2024-89.jpg",
  "../img/slider/ISIF 2024-253.jpg",
  "../img/slider/ISIF 2024-294.jpg",
  "../img/slider/ISIF 2024-45.jpg",
  "../img/slider/ISIF 2024-58.jpg",
];

const ImageSlider = () => {
  return (
    <div id="imageSlider" className="carousel slide" data-bs-ride="carousel">
      <div className="carousel-inner">
        {images.map((src, index) => (
          <div
            className={`carousel-item ${index === 0 ? 'active' : ''}`}
            key={index}
          >
            <img src={src} className="d-block w-100" alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </div>

      <button
        className="carousel-control-prev"
        type="button"
        data-bs-target="#imageSlider"
        data-bs-slide="prev"
      >
        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Previous</span>
      </button>
      <button
        className="carousel-control-next"
        type="button"
        data-bs-target="#imageSlider"
        data-bs-slide="next"
      >
        <span className="carousel-control-next-icon" aria-hidden="true"></span>
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
};

export default ImageSlider;
